import React from 'react';
import { Route } from 'react-router-dom'
import CollectionsOverview from "../../components/collections-overview/collections-overview.component";

import CollectionPage from "../collection/collection.component";
import WithSpinner from "../../components/with-spinner/with-spinner.component";

import {firestore, convertCollectionSnapshotToMap} from "../../firebase/firebase.utils";
import { connect } from 'react-redux';
import { UpdateCollections } from "../../redux/shop/shop.actions";

class  ShopPage extends React.Component{

    // const CollectionOverviewWithSpinner = WithSpinner(CollectionsOverview);
    // const Collection

    state = { //we don't need to call constructor, if we do it like this.
        loading: true
    };


    unSubscribeFromSnapshot = null;

    componentDidMount() {
        const {updateCollections } = this.props;
        const collectionRef = firestore.collection('collections');

        collectionRef.onSnapshot(async snapshot => {
            const collectionMap = convertCollectionSnapshotToMap(snapshot);
            updateCollections(collectionMap);
            this.setState({loading: false})
        })
    }

    render() {
        const { match } = this.props; //one of the three props Route passed into ShopPage in App.js
        return(
            <div className='shop-page'>
                <Route exact path={`${match.path}`} component={CollectionsOverview} />
                <Route path={`${match.path}/:collectionId`} component={CollectionPage}/> {/*if we want to make the url varying we don't want to make Route of ShopPage *exact* */}
            </div>
        )
    }


}

const mapDispatchToProps = dispatch => ({
    updateCollections: collectionMap => dispatch(UpdateCollections(collectionMap))
});

export default connect(null, mapDispatchToProps)(ShopPage);
