import React from 'react';
import './collection-preview.styles.scss'
import CollectionItem from "../collection-item/collection-item.components";
import {withRouter} from 'react-router-dom';

const CollectionsPreview = ({title,items, history, match}) => {
    const responsiveTotalItems = () => {
        if (window.matchMedia("(max-width: 1800px)")) {
            return 4
        }
        if (window.matchMedia("(max-width: 1200px)")) {
            return 3
        }
        if (window.matchMedia("(max-width: 600px)")) {
            return 2
        }
    };
    return(
        <div className='collection-preview'>
            <h1 className='title' onClick={() => history.push(`${match.url}/${title.toLowerCase()}`)}> {title.toUpperCase()}</h1>
            <div className='preview'>
                {items
                    .filter((item,index) => index < responsiveTotalItems())//making sure there are only 4 items each (remember index starts from 0)
                    .map(item => (
                        <CollectionItem key={item.id} item={item}/>
                    ))
                }
            </div>
        </div>
)};

export default withRouter(CollectionsPreview);
