import React from 'react';
import './App.css';
import {Switch,Route} from 'react-router-dom';
import Homepage from "./pages/homepage/Homepage.components";
import ShopPage from "./pages/shop/shop.component";
import Header from "./components/header/header.component";
import SignUpAndSignInPage from "./pages/sign-up-and-sign-in/sign-up-and-sign-in.component";
import {auth, createUserProfileDocument} from "./firebase/firebase.utils";

class App extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            currentUser: null
        }
    }

    HatsPage = () => (
        <div>
            <h1>Hats Page</h1>
        </div>
    );
    unSubscribeFromAuth = null;
    componentDidMount() {
        this.unSubscribeFromAuth = auth.onAuthStateChanged(async userAuth => {
            if (userAuth){
                const userRef = await createUserProfileDocument(userAuth); //userRef getting back by sending userAuth from createUserProfileDocument method we created which is all the information of the user logged in

                userRef.onSnapshot(snapShot => { //getting user's data
                    //console.log(snapShot.data()); //snapShot.data gives back all the information stored in for the user in database
                    this.setState({
                        currentUser: {
                            id: snapShot.id,

                        }
                    })
                })
            }
        });
    }

    componentWillUnmount() {
        this.unSubscribeFromAuth(); //to prevent memory leaks// when we unmount our app, we want to clear auth // this is not equal to signOur
    }

    render() {
        return (
            <div>
                <Header currentUser={this.state.currentUser}/>
                <Switch> {/*switch render the first component the path matches and ignore the rest */}
                    <Route exact path='/' component={Homepage}/>
                    <Route exact path='/hats' component={this.HatsPage}/>
                    <Route exact path='/shop' component={ShopPage}/>
                    <Route exact path='/signin' component={SignUpAndSignInPage}/>

                </Switch>
            </div>
        );
    }
}


export default App;
