import React, {useState} from 'react';

import FormInput from "../formInput/formInput.component";
import CustomButton from "../custom-button/custom-button.component";

import { connect } from 'react-redux';
import { signUpStart } from "../../redux/user/user.actions";

import './sign-up.styles.scss';

const SignUp = ({ signUpStart }) => { //using hooks
    const [useCredentials, setCredentials] = useState({displayName: '', email: '', password: '', confirmPassword: ''});
    const {displayName, email, password, confirmPassword } = useCredentials;

    const handleSubmit = async event => {
        event.preventDefault();
        if(password !== confirmPassword){
            alert(`passwords don't match`);
            return;
        }

        signUpStart({displayName, email, password});

        setCredentials({
            displayName: '',
            email: '',
            password: '',
            confirmPassword: ''
        }); // to clear our form

    };

    const handleChange = event => {
        const { name, value} =event.target;

        setCredentials({...useCredentials, [name]: value})
    };

    return(
        <div className='sign-up' >
            <h2 className='title'>I do not have an account</h2>
            <span>Sign up with your email and password</span>
            <form className='sign-up-form' onSubmit={handleSubmit}>
                <FormInput
                type='text'
                name='displayName'
                value={displayName}
                onChange={handleChange}
                label='Display Name'
                required
                />
                <FormInput
                    type='email'
                    name='email'
                    value={email}
                    onChange={handleChange}
                    label='Email'
                    required
                />
                <FormInput
                    type='password'
                    name='password'
                    value={password}
                    onChange={handleChange}
                    label='Password'
                    required
                />
                <FormInput
                    type='password'
                    name='confirmPassword'
                    value={confirmPassword}
                    onChange={handleChange}
                    label='Confirm Password'
                    required
                />
                <CustomButton type='submit'>Sign Up</CustomButton>
            </form>
        </div>
    )
};

const mapDispatchToProps = dispatch => ({
    signUpStart: userCredentials => dispatch(signUpStart(userCredentials))
});

export default connect(null, mapDispatchToProps)(SignUp);
