import { takeEvery } from 'redux-saga/effects'
import ShopActionTypes from "./shop.types";

export function* fetchCollectionsAsync() { //using generator function
    yield console.log('I am fired')
}

export function* fetchCollectionsStart() { //this function is for using in redux store // to use in redux component, we use really action (function) with the same name as this// which we usually write in our actions file
    yield takeEvery(
        ShopActionTypes.FETCH_COLLECTIONS_START,
        fetchCollectionsAsync
    )
}
